mapper.select:
select setmeal.*,category.name as categoryName  from sky_take_out.setmeal left join sky_take_out.category on setmeal.category_id = category.id


        and  setmeal.name like concat('%',#{name},'%')


        and setmeal.category_id = #{categoryId}


        and setmeal.status = #{status}


order by setmeal.create_time desc
